import React, { useState, useEffect } from 'react';
import axios from "axios"
import { Link } from "react-router-dom";
//import './creacion.css'
export function Creacion() {
const[state, setState] = useState({
        nombre: '',
        peso: '',
        altura: '',
        vida: '',
        temperamento: '',
        imagen: '',
        exists: false, 
        success: false,
        chooseTemps: undefined,
        hidden: true
    })   
    // eslint-disable-next-line
    useEffect(() => {
      async function getNames(){
      const names = await axios.get("http://localhost:3001/temperaments")
      setState({
        ...state,
        chooseTemps: names.data
      })
      }
      getNames()
      // eslint-disable-next-line
    }, [])

    const handleChange = () => {
        setState({
            ...state,
            nombre: document.querySelector('input[name=nombre]').value,
            peso: document.querySelector('input[name=peso]').value,
            altura: document.querySelector('input[name=altura]').value,
            vida: document.querySelector('input[name=vida]').value,
            temperamento: document.querySelector('input[name=temperamento]').value,
            imagen: document.querySelector('input[name=imagen]').value,
            success: false,
        })
    }
    const handleSubmit = async e => {        
        e.preventDefault()
        const names = await axios.get("http://localhost:3001/dogs")
        if(names.data.includes(state.nombre)) {
          setState({
            ...state,
            exists:true
          })
        }
        else {
            setState({
            ...state,
            exists:false
            })
            axios.post("http://localhost:3001/dogs", state)
            alert('Breed created')
        }             
      }
   /* const handleTemps = () => { 
      if(state.hidden === true){
        setState({
          ...state,
          hidden: false
        })
      }
      else{
        setState({
          ...state,
          hidden: true
        })
      }
    } */
    const addTemp = (e) => {
      if(!state.temperamento.includes(e.target.value)){
        if(state.temperamento === ''){
          setState({
            ...state,
            temperamento: e.target.value
          })
        }
        else{
        setState({
          ...state,
          temperamento: state.temperamento.concat(', ' + e.target.value)
        })
        } 
      }
      else{
        if(state.temperamento.includes(', ' + e.target.value))
        setState({
          ...state,
          temperamento: state.temperamento.replace(', ' + e.target.value, "", "gi")
        })
        else if(state.temperamento.includes(e.target.value + ', '))
        setState({
          ...state,
          temperamento: state.temperamento.replace(e.target.value + ', ', "", "gi")
        })
        else 
        setState({
          ...state,
          temperamento: state.temperamento.replace(e.target.value, "", "gi")
        })
      }
    }

    return (
      <div className='formBox'>
        <Link to='/home'>
            <button className='btnCreOut'>Back</button>
        </Link>
        <div className='form'>
        <span>(*) required fields</span><br/>&nbsp;
        <form  onSubmit={(e) =>handleSubmit(e)}>
          <div className='formItem'>
            <span>Name</span>&nbsp;
            <input className={state.exists? 'errorImput' : undefined} type="text" onChange={handleChange} value={state.nombre} name='nombre' placeholder='name' required />
            &nbsp;<span className='aligned'>*</span> 
          </div>        
          <div className='formItem'>
              <span>Temperament:</span>&nbsp;
              <input type="text" onChange={handleChange} value={state.temperamento} name='temperamento' placeholder='temp1, temp2, etc' required />
              &nbsp;<span>*</span>&nbsp;                
          </div>    
          <div className='formItem'>
              <span>Weight(lb)</span>&nbsp;
              <input className='shorterImput' type="number" min="0" onChange={handleChange} value={state.peso} name='peso' placeholder='' required />
              &nbsp;<span className='aligned'>*</span> 
          </div>          
          <div className='formItem'>
              <span>Height(inches)</span>&nbsp;
              <input className='shorterImput' type="number" min="0" onChange={handleChange} value={state.altura} name='altura' placeholder='' required />
              &nbsp;<span className='aligned'>*</span> 
          </div>          
          <div className='formItem'>
              <span>Life span(years)</span>&nbsp;
              <input className='shorterImput' type="number" min="0" onChange={handleChange} value={state.años_vida} name='vida' placeholder='' required />
              &nbsp;<span className='aligned'>*</span> 
          </div>          
          <div className='formItem'>
              <span>Image</span>&nbsp;
              <input type="url" onChange={handleChange} value={state.image} name="image" placeholder="Url"/>
          </div>
          <div>
          <input className='btnCre' type='submit' value='Add'/>
          </div>
          <div>
          <span className='errorMsg'>{state.exists && <span className='errorText'>A breed with that name already exists</span>}</span>
          </div>
        </form>
        </div>
        <div className='tempsContainer'>
                {state.chooseTemps && !state.hidden && state.chooseTemps.map(e => {
                  return(
                    <div>  
                    <label for={e}>{e}</label>
                    <input onChange={(e) => addTemp(e)} type="checkbox" name={e} value={e} />    
                    </div>
                    )
                  })            
                }    
              </div> 
      </div>
    )
}
