import React, { useState, useEffect } from 'react';
import axios from "axios"
import { Link } from "react-router-dom";
//import './creacion.css'

const[state, setState] = useState({
        nombre: '',
        peso: '',
        altura: '',
        vida: '',
        temperamento: '',
        peso: '',
        altura: '',
        vida: '',
        imagen: '',
        exists: false, 
        success: false,
        chooseTemps: undefined,
        hidden: true
    })   

    useEffect(() => {
      async function getNames(){
      const names = await axios.get("http://localhost:3001/temperaments")
      setState({
        ...state,
        chooseTemps: names.data
      })
      }
      getNames()
    }, [])

    const handleChange = () => {
        setState({
            ...state,
            nombre: document.querySelector('input[name=nombre]').value,
            peso: document.querySelector('input[name=pesoMin]').value,
            altura: document.querySelector('input[name=alturaMin]').value,
            vida: document.querySelector('input[name=vidaMin]').value,
            temperamento: document.querySelector('input[name=temperamento]').value,
            imagen: document.querySelector('input[name=imagen]').value,
            success: false,
        })
    }
